mapper.select:
select * from (select rownum rnum ,board_num,id,category_id,content,write_date,recommend_num,report_num,screp,comment_num from (select * from board where category_id IN #{item}
AND board_num NOT IN #{item}
ORDER BY write_date DESC) board ORDER BY write_date DESC) where rnum between 1 and 3 ORDER BY write_date DESC


select * 
from (select rownum rnum ,board_num,id,category_id,content,write_date,recommend_num,report_num,screp,comment_num 
	from (select * from board where board_num<#{lastBoard_num}
		AND category_id IN 
			 #{item} 

		AND board_num NOT IN
			 #{item} 

		ORDER BY write_date DESC)
	board ORDER BY write_date DESC) 
where rnum between 1 and 3 ORDER BY write_date DESC
